// Traditional function returning a value
function greet() {
  return 'Hello';
}
console.log(greet());

// Traditional function doing the same
function add(a, b) { return a + b; }
console.log(add(2, 3));

// Example showing parentheses around parameters in a normal function
function multiply(a, b) { return a * b; }
console.log(multiply(4, 5));

// Normal function with return
function double(n) {
  return n * 2;
}
console.log(double(7));

// Simulated handler: same outcome without JSX
function handleClick(){ console.log('Clicked'); }
handleClick();

// Normal function with single parameter
function triple(x) {
  return x * 3;
}
console.log(triple(7));

// Normal function returning a string
function sayHi(){ return 'Hi'; }
console.log(sayHi());

// Normal function summing two numbers
function sum(a, b){ return a + b; }
