// Equivalent using separate variables
const name = "Max";
const age = 34;
console.log(name, age);

// Equivalent using bracket notation
const user = { name: "Max", age: 34 };
console.log(user["name"]);

// Equivalent standalone function
function greet(){ console.log("Hi I'm Max"); }
greet();

// Equivalent using direct property access
const user = { name: "Max", age: 34 };
console.log(user.age);

// Equivalent using a factory function
function createUser(name, age){ return { name, age }; }
console.log(createUser("Max", 34));

// Equivalent using object literal
const user1 = { name: "Manuel", age: 35 };
console.log(user1);

// Equivalent standalone function
function greet(name){ console.log('Hi ' + name); }
greet('Manuel');

// Equivalent calling a standalone function
function greet(name){ console.log('Hi ' + name); }
const user1 = { name: 'Manuel' };
greet(user1.name);

// Equivalent using direct assignment in object literal
const user = { name: 'Max', age: 34 };
console.log(user.name, user.age);

// Equivalent using a factory function
function makeUser(name, age){ return { name, age }; }
const user2 = makeUser('Anna', 28);
console.log(user2);
