// Equivalent using individual variables
const hobby1 = "Sports";
const hobby2 = "Cooking";
const hobby3 = "Reading";
console.log(hobby1, hobby2, hobby3);

// Equivalent using destructuring
const hobbies = ["Sports", "Cooking", "Reading"];
const [first] = hobbies;
console.log(first);

// Equivalent using spread operator
let hobbies = ["Sports", "Cooking", "Reading"];
hobbies = [...hobbies, "Working"];
console.log(hobbies);

// Equivalent using slice
const numbers = [4,8,12,16];
const shortened = numbers.slice(0, -1);
console.log(shortened);

// Equivalent using slice
const numbers = [4,8,12,16];
const shortened = numbers.slice(1);
console.log(shortened);

// Equivalent using concat
const numbers = [4,8,12,16];
const extended = numbers.concat(20);
console.log(extended);

// Equivalent using spread operator
const numbers = [4,8,12,16];
const extended = [2, ...numbers];
console.log(extended);

// Equivalent using indexOf
const hobbies = ["Sports", "Cooking", "Reading", "Working"];
console.log(hobbies.indexOf("Sports"));

// Equivalent using for loop
const hobbies = ["Sports", "Cooking", "Reading", "Working"];
let idx = -1;
for(let i=0;i<hobbies.length;i++){
  if(hobbies[i]==="Sports"){ idx=i; break; }
}
console.log(idx);

// Equivalent using for loop
const hobbies = ["Sports", "Cooking", "Reading", "Working"];
const newhobbies = [];
for(const h of hobbies){ newhobbies.push(h+"!"); }
console.log(newhobbies);

// Equivalent using for loop
const hobbies = ["Sports", "Cooking", "Reading", "Working"];
const newobjects = [];
for(const h of hobbies){ newobjects.push({ text: h }); }
console.log(newobjects);

// Equivalent using for loop
const numbers = [1,2,3,4,5];
const evens = [];
for(const n of numbers){ if(n%2===0) evens.push(n); }
console.log(evens);

// Equivalent using for loop
const numbers = [1,2,3,4];
let sum = 0;
for(const n of numbers){ sum += n; }
console.log(sum);

// Equivalent using for loop
const numbers = [4,8,12,16];
let result = false;
for(const n of numbers){ if(n>10){ result=true; break; } }
console.log(result);

// Equivalent using for loop
const numbers = [2,4,6];
let allEven = true;
for(const n of numbers){ if(n%2!==0){ allEven=false; break; } }
console.log(allEven);

// Equivalent using indexOf
const hobbies = ["Sports","Cooking","Reading"];
console.log(hobbies.indexOf("Cooking") !== -1);

// Equivalent using for loop
const numbers = [4,8,12,16];
let found;
for(const n of numbers){ if(n>10){ found=n; break; } }
console.log(found);

// Equivalent using concat and spread
const nested = [1,[2,3],[4]];
const flattened = [].concat(...nested);
console.log(flattened);

// Equivalent using length property
const numbers = [4,8,12,16];
console.log(numbers[numbers.length-1]);

// Equivalent using slice + sort
const numbers = [3,1,2];
const sorted = numbers.slice().sort();
console.log(sorted);
console.log(numbers);
