// Equivalent using regex
const text = "   hello   ";
const trimmed = text.replace(/^\s+|\s+$/g, "");
console.log(trimmed);

// Equivalent using manual loop
const word = "hello";
let upper = "";
for(const ch of word){ upper += ch.toLocaleUpperCase(); }
console.log(upper);

// Equivalent using manual loop
const word = "HELLO";
let lower = "";
for(const ch of word){ lower += ch.toLocaleLowerCase(); }
console.log(lower);

// Equivalent using regex match
const sentence = "I love coding";
const words = sentence.match(/\S+/g);
console.log(words);

// Equivalent using indexOf + slice
const text = "dog and dog";
const idx = text.indexOf("dog");
const replaced = text.slice(0, idx) + "cat" + text.slice(idx+3);
console.log(replaced);

// Equivalent using slice
const text = "Hello world";
console.log(text.slice(0,5) === "Hello");

// Equivalent using slice
const text = "Hello world";
console.log(text.slice(-5) === "world");

// Equivalent using loop
const word = "Hi";
let repeated = "";
for(let i=0;i<3;i++){ repeated += word; }
console.log(repeated);

// Equivalent using indexOf
const text = "I love code";
console.log(text.indexOf("code") !== -1);

// Equivalent using length property
const text = "Hello";
console.log(text[text.length-1]);
